/** @jsx jsx */
import { jsx } from '@emotion/react';
import INotification from '@magicbell/react-headless/dist/types/INotification';
export interface Props {
    notification: INotification;
    menuPlacement?: 'auto' | 'auto-start' | 'auto-end' | 'top' | 'bottom' | 'right' | 'left' | 'top-start' | 'top-end' | 'bottom-start' | 'bottom-end' | 'right-start' | 'right-end' | 'left-start' | 'left-end';
}
/**
 * Component that renders a dot if the notification is unread and an icon for
 * the context menu otherwise. In any case, the context menu can be opened on
 * click.
 *
 * @example
 * <NotificationState notification={notification} />
 */
export default function NotificationState({ notification, menuPlacement }: Props): jsx.JSX.Element;
